import fonts from "./fonts.js";
import quotes from "./quotes.js";

/* import Template from "./template.js"; */

/**
 * Handle all events between the DOM and {@link fonts}
 *
 */
const EventHandler = () => {
	let gridList = document.querySelector(".grid-list");

	const togglethemeBtn = document.querySelector(".toggle-theme__btn");
	let toggleColor = false;
	const toggleViewBtn = document.querySelector(".toggle-view__btn");

	const inputFontTyped = document.querySelector(".type-something__input");
	let spanEditable = document.querySelectorAll(".spanEditable");
	const searchFont = document.querySelector(".search-font__input");

	let fontInfoContainer = document.querySelectorAll(".font-info");
	const goToTop = document.getElementById("go-to-top");

	const changeFontSize = document.querySelector(".change-font-size");
	const changeFontSizeBtn = document.querySelector(".change-font-size__btn");
	let fontsSizeOptions = document.querySelectorAll(".font-size-option");
	const refreshBtn = document.querySelector(".refresh__btn");

	let addFonts = document.querySelectorAll(".add-font");

	/* const head = document.getElementsByTagName("head")[0];
	const link = document.createElement("link"); */
	/* const changeFontSize = document.querySelector(".down-arrow"); */

	const refreshDOM = () => {
		fontInfoContainer = document.querySelectorAll(".font-info");
		spanEditable = document.querySelectorAll(".spanEditable");
		addFonts = document.querySelectorAll(".add-font");
	};

	// *-----* Font size handler *-----* //

	// * Loop over font size button to fire a click event listener on each of them and update the font size in each spanEditable
	for (let fontSizeOption of fontsSizeOptions) {
		fontSizeOption.addEventListener("click", e => {
			changeFontSizeBtn.textContent = `${e.target.textContent}px`;
			for (let s of spanEditable) {
				s.style.fontSize = `${e.target.textContent}px`;
			}
		});
	}

	// *  Initialize button text-contentand spanEditable font size
	changeFontSizeBtn.textContent = "40px";
	const initFontSize = () => {
		refreshDOM();
		for (let s of spanEditable) {
			s.style.fontSize = `${changeFontSizeBtn.textContent}`;
		}
	};
	initFontSize();

	// * Display the fonts size choice on click event
	changeFontSize.addEventListener("click", () => {
		document.querySelector(".show-size").style.display === "block"
			? (document.querySelector(".show-size").style.display = "none")
			: (document.querySelector(".show-size").style.display = "block");
	});

	// * refresh page with refresh button as if it was reloaded
	refreshBtn.addEventListener("click", () => {
		if (document.body.hasAttribute("black"))
			document.body.removeAttribute("black");
		if (gridList.hasAttribute("wide")) gridList.removeAttribute("wide");
		/* fonts.fontsList = []; */
		fonts.refresh();
		changeFontSizeBtn.textContent = "40px";
		initFontSize();
		/* fonts.fontsListIndexOnScroll = 0; */
		/* fonts.fontsListIndexOnScroll = 0; */
		console.log(fonts.fontsList);

		searchFont.value = "";
		inputFontTyped.value = "";
		fonts.research = false;
		addFontsOnLocalStorage();
	});

	const addFontsOnLocalStorage = () => {
		refreshDOM();
		for (let addFont of addFonts) {
			addFont.addEventListener("click", e => {
				let retrieveFontFamily = e.target.previousSibling.innerHTML;

				if (addFont.textContent === "add_circle_outline") {
					fonts.storeFont(retrieveFontFamily);
					addFont.classList.add("animate");
					addFont.classList.add("added");
					setTimeout(() => {
						addFont.textContent = "remove_circle_outline";
						addFont.classList.remove("animate");
					}, 500);
				} else if (addFont.textContent === "remove_circle_outline") {
					fonts.removeFont(retrieveFontFamily);
					addFont.classList.add("animate");
					setTimeout(() => {
						addFont.textContent = "add_circle_outline";
						addFont.classList.remove("animate");
					}, 500);
				}
				/* addFont.textContent = "remove_circle_outline" */
				/* addFont.classList.remove("test"); */
			});
		}
	};
	addFontsOnLocalStorage();

	/* for (let addFont of addFonts) {
		console.log("qdfqf");
		if (addFont.classList.contains("added")) {
			addFont.textContent = "remove_circle_outline";
		}
	} */

	togglethemeBtn.addEventListener("click", () => {
		const body = document.body;
		if (toggleColor) {
			body.removeAttribute("black");
		} else {
			body.setAttribute("black", "");
		}
		toggleColor = !toggleColor;
	});

	const getRandomQuotes = () => {
		let randomNb = Math.floor(Math.random() * quotes.length);
		return quotes[randomNb];
	};

	inputFontTyped.addEventListener("input", e => {
		spanEditable = document.querySelectorAll(".spanEditable");
		for (let i = 0; i < spanEditable.length; i++) {
			if (e.target.value.length === 0) {
				spanEditable[i].textContent = `${getRandomQuotes()}`;
			} else {
				spanEditable[i].textContent = e.target.value;
			}
		}
	});

	toggleViewBtn.addEventListener("click", () => {
		gridList.hasAttribute("wide")
			? gridList.removeAttribute("wide")
			: gridList.setAttribute("wide", "");
	});

	window.onscroll = () => {
		onScroll();
	};

	const onScroll = () => {
		let toolbar = document.querySelector(".global-toolbar");
		if (
			document.body.scrollTop > 20 ||
			document.documentElement.scrollTop > 20
		) {
			goToTop.style.display = "block";
			toolbar.setAttribute("fixed-nav", "");
		} else {
			goToTop.style.display = "none";
			toolbar.removeAttribute("fixed-nav");
		}
	};

	goToTop.addEventListener("click", () => {
		scrollToTop();
	});

	const scrollToTop = () => {
		const c = document.documentElement.scrollTop || document.body.scrollTop;
		if (c > 0) {
			window.requestAnimationFrame(scrollToTop);
			window.scrollTo(0, c - c / 7);
		}
	};

	let timerId;
	searchFont.addEventListener("input", e => {
		var debounceFunction = function(func, delay) {
			// Cancels the setTimeout method execution
			clearTimeout(timerId);

			// Executes the func after delay time.
			timerId = setTimeout(func, delay);
		};

		function makeAPICall() {
			fonts.refresh();
			refreshDOM();
			initFontSize();
			if (e.target.value.length === 0) {
				/* fonts.refresh();
				refreshDOM();
				initFontSize(); */
				fonts.research = false;
				if (inputFontTyped.value.length > 0) {
					for (let s of spanEditable) {
						s.textContent = inputFontTyped.value;
					}
				}
				addFontsOnLocalStorage();
			} else {
				const filterFonts = fonts.stock.items.filter((
					font // or fonts.stock.items.filter
				) => font.family.toLowerCase().includes(e.target.value.toLowerCase()));
				for (let filterFont of filterFonts) {
					fonts.handleFontsLoad(filterFont);
				}

				let search = true;
				fonts.createFontContainer(filterFonts, search);
				initFontSize();
				if (inputFontTyped.value.length > 0) {
					for (let s of spanEditable) {
						s.textContent = inputFontTyped.value;
					}
				}
				addFontsOnLocalStorage();
			}
			/* fonts.fontsListIndexOnScroll = 5; */
		}

		// Debounces makeAPICall method
		debounceFunction(makeAPICall, 2000);
	});

	// * Load fonts an scroll
	document.addEventListener("scroll", e => {
		/* console.log(window.pageYOffset); */

		let t = document.querySelector("footer");
		if (window.pageYOffset <= 100) {
			t.style.display = "flex";
		} else {
			t.style.display = "none";
		}

		if (fonts.research === true) return;

		let endPointScroll =
			0 + document.body.clientHeight - window.innerHeight - window.scrollY;
		if (endPointScroll === 0) return;
		if (endPointScroll < 200) {
			refreshDOM();
			/* fonts.fontsList = [] */
			/* console.log(fonts.fontsList.length);
			if (fonts.fontsList.length === 15) {
				gridList.innerHTML = "";
			} */
			/* console.log(endPointScroll); */

			fonts.handleFontsLoad("scroll");
			initFontSize();
			if (inputFontTyped.value.length > 0) {
				for (let s of spanEditable) {
					s.textContent = inputFontTyped.value;
				}
			}
			addFontsOnLocalStorage();
			console.log(fonts.fontsList);
			console.log(fonts.stock);
		}
	});
};

export default EventHandler;

import Template from "./template.js";
import EventHandler from "./eventHandler.js";
import { config } from "../config.js";

/**
 * Create a Fonts object which handle and collect all fonts from google fonts API
 *
 * @example
 * let fonts = new Fonts();
 */
export class Fonts {
	/**
	 * @constructor
	 */
	constructor() {
		/**
		 * @type {Object}
		 */
		(this.fontsList = []),
			/**
			 * @type {Object}
			 */
			(this.stock = []),
			/**
			 * @type {Number}
			 */
			(this.fontsListIndex = 0),
			/**
			 * @type {Number}
			 */
			(this.fontsListIndexOnScroll = 0),
			/**
			 * @type {Number}
			 */
			(this.incrementor = 15),
			/**
			 * @type {string}
			 */
			(this.apiURL = ["https://fonts.googleapis.com/css?family="]),
			/**
			 * @type {boolean}
			 */
			(this.research = false),
			/**
			 * @type {Object}
			 */
			(this.gridList = document.querySelector(".grid-list")),
			/**
			 * @type {Object}
			 */
			(this.localStorageFont = []),
			/**
			 * @type {Object}
			 */
			(this.finalURL = []);
	}
	/**
	 *  Fetch the fonts from google fonts api and save theme in stock array property
	 *
	 */
	async getGFonts() {
		const jsonFonts = await fetch(
			`https://www.googleapis.com/webfonts/v1/webfonts?sort=popularity&key=${config.apiKey}`
		);
		try {
			const gFonts = await jsonFonts.json();
			this.stock = gFonts;
			await this.handleFontsLoad();
		} catch (error) {
			console.log(error);
		}
		await EventHandler();
	}

	/**
	 * Store font that has been clicked in localStorage
	 *
	 * @param {string} font Represents the font-family name
	 */
	storeFont(font) {
		if (localStorage.getItem("fonts")) {
			let getFontFromLocalStorage = JSON.parse(localStorage.getItem("fonts"));
			if (getFontFromLocalStorage.includes(font)) return;
			getFontFromLocalStorage.push(font);
			localStorage.setItem("fonts", JSON.stringify(getFontFromLocalStorage));
		} else {
			this.localStorageFont.push(font);
			localStorage.setItem("fonts", JSON.stringify(this.localStorageFont));
		}
	}

	/**
	 * Remove font from localStorage and update it in stock as well to handle state of add/remove button
	 *
	 * @param {string} font Represents the font-family name
	 */
	removeFont(font) {
		let getFontFromLocalStorage = JSON.parse(localStorage.getItem("fonts"));
		getFontFromLocalStorage.map((f, i) => {
			if (f === font) {
				getFontFromLocalStorage.splice(i, 1);
				this.stock.items.map(ft => {
					if (ft.family === f) delete ft.added;
				});
			}
		});
		localStorage.setItem("fonts", JSON.stringify(getFontFromLocalStorage));
	}

	/**
	 *  Handle the display of saved fonts by getting the fonts from localStorage and placing them at the begining of the stock array property making shure there is no doublon
	 */
	getFontFromLocalStorage() {
		let getFontFromLocalStorage = localStorage.getItem("fonts");
		for (let f of JSON.parse(getFontFromLocalStorage)) {
			this.stock.items.map((el, i) => {
				if (el.family === f) {
					this.stock.items.splice(i, 1);
					el.added = "added";
					this.stock.items.splice(0, 0, el);
				}
			});
		}
	}

	/**
	 *  Handle fonts load according to the parameter: scroll, value of input search or the initial loading fonts
	 *
	 * @param {string} newFontsResearch Represents either "scroll" or input search result
	 */
	handleFontsLoad(newFontsResearch) {
		if (newFontsResearch === "scroll") {
			this.setUpFonts(this.fontsListIndexOnScroll, this.incrementor, true);
		} else if (newFontsResearch) {
			this.research = true;
			this.handleContainerAndURL(newFontsResearch);
			this.setUpFonts(this.fontsListIndex, this.incrementor, false);
		} else {
			if (localStorage.fonts && localStorage.fonts.length > 0) {
				this.getFontFromLocalStorage();
			}
			this.setUpFonts(this.fontsListIndex, this.incrementor, true);
		}
	}

	/**
	 * Handle the loads of fonts according to the boolean value
	 *
	 * @param {number} fontListIndex Respresents the index of iteration through the font list
	 * @param {number} incrementor Represent the number that increment the {@link fontListIndex}
	 * @param {boolean} bool Represent the boolean that indicate how to handle the iteration if either search is: an initial loading fonts, a scroll or an input search
	 */
	setUpFonts(fontListIndex, incrementor, bool) {
		bool
			? this.loopOverFontList(fontListIndex, incrementor)
			: (fontListIndex += incrementor);
		this.fontsListIndexOnScroll += incrementor;
		this.createURL();
	}

	/**
	 *
	 * @param {number} fontListIndex Respresents the index of iteration through the font list
	 * @param {number} incrementor Represent the number that increment the {@link fontListIndex}
	 */
	loopOverFontList(fontListIndex, incrementor) {
		for (let i = fontListIndex; i < fontListIndex + incrementor; i++) {
			this.handleContainerAndURL(this.stock.items[i]);
		}
	}

	/**
	 *
	 * @param {Object} fontItem Object return from the google font API
	 */
	handleContainerAndURL(fontItem) {
		this.fontsList.push(fontItem);
		this.createFontContainer(fontItem);
		this.handleUrlFonts(fontItem);
	}

	/**
	 *  Refresh the page as if it was reloaded
	 */
	refresh() {
		this.fontsList = [];
		this.fontsListIndex = 0;
		this.gridList.innerHTML = "";
		this.fontsListIndexOnScroll = 0;
		this.incrementor = 15;
		window.scrollTo(0, 0);
		if (localStorage.fonts && localStorage.fonts.length > 0) {
			this.getFontFromLocalStorage();
		}
		this.setUpFonts(this.fontsListIndex, this.incrementor, true);

		this.createURL();
	}

	/**
	 * Create a font template for each fonts
	 *
	 * @param {Object} font Object return from the google font API
	 * @param {boolean} search Boolean to know if the query is from research or not
	 */
	createFontContainer(font, search) {
		if (search) {
			this.gridList.innerHTML = "";
			for (let f of font) {
				const templateFont = Template(f);
				this.gridList.appendChild(templateFont);
			}
		} else {
			const templateFont = Template(font);
			this.gridList.appendChild(templateFont);
		}
	}

	/**
	 *  Format each font name before creating an url
	 *
	 * @param {Object} fontsList Object return from the google font API
	 */
	handleUrlFonts(fontsList) {
		let regexSpace = / /g;
		let formattedFont = fontsList.family.replace(regexSpace, "+");
		this.apiURL.push(formattedFont);
	}

	/**
	 * Createand format a final url with all font name
	 */
	createURL() {
		this.finalURL = [];
		this.apiURL.map((name, index, arr) => {
			index === 0 || index === 1
				? this.finalURL.push(name)
				: index === arr.length - 1
				? this.finalURL.push(`|${name}&display=swap`)
				: this.finalURL.push(`|${name}`);
		});

		let finalURL = this.finalURL.join("");
		let linkUrl = document.createElement("link");
		linkUrl.setAttribute("href", `${finalURL}`);
		linkUrl.setAttribute("rel", "stylesheet");
		linkUrl.setAttribute("id", "fonts-url");

		let HTMLlink = document.getElementById("fonts-url");

		if (HTMLlink) {
			HTMLlink.remove();
			document.head.appendChild(linkUrl);
		} else document.head.appendChild(linkUrl);
	}
}

let fonts = new Fonts();

export default fonts;

import quotes from "./quotes.js";
/* import fonts from "./fonts.js"; */

/**
 *
 * @param {Object} fonts Font object as it is return by Google Font API
 * @param {string} fonts.family Fonts family property
 * @param {string} fonts.category Fonts category property
 *
 */
const HTMLTemplate = fonts => {
	let fontInfoContainer = document.createElement("div");
	let flexContainer = document.createElement("div");
	let fontName = document.createElement("h1");
	let categoryName = document.createElement("h2");
	let addIcon = document.createElement("i");
	let spanEditable = document.createElement("span");
	let randomNb = Math.floor(Math.random() * quotes.length);
	let randomQuote = quotes[randomNb];

	fontInfoContainer.classList.add("font-info");
	flexContainer.classList.add("flex");
	addIcon.classList.add("material-icons");
	addIcon.classList.add("add-font");

	if (fonts.added) addIcon.textContent = "remove_circle_outline";
	else addIcon.textContent = "add_circle_outline";

	spanEditable.classList.add("spanEditable");
	spanEditable.setAttribute("contenteditable", "true");
	spanEditable.textContent = `${randomQuote}`;
	spanEditable.style.fontFamily = fonts.family;
	fontName.textContent = fonts.family;
	fontName.style.fontFamily = fonts.family;

	fonts.variants
		? (categoryName.textContent = `${fonts.category}, ${fonts.variants.length} variants`)
		: "No variants";

	fontInfoContainer.appendChild(flexContainer);
	flexContainer.appendChild(fontName);
	flexContainer.appendChild(addIcon);
	fontInfoContainer.appendChild(categoryName);
	fontInfoContainer.appendChild(spanEditable);

	return fontInfoContainer;
};

export default HTMLTemplate;
